INSERT INTO kitaplar (kitap_adi, yazar, basim_yili, kategori, sayfa_sayisi, basim_yeri, isbn, tanitim, kapak_url) VALUES
('Suç ve Ceza', 'Fyodor Dostoyevski', 1866, 'Klasik Edebiyat', 671, 'Sankt Petersburg, Rusya', 9789750767890, 'Dünya edebiyatının başyapıtlarından biri. Rodion Raskolnikov adlı genç bir öğrencinin işlediği cinayet sonrası yaşadığı psikolojik çöküşü ve vicdani hesaplaşmasını anlatan bu roman, dünya edebiyatının en derin psikolojik analizlerinden biridir.', 'https://covers.openlibrary.org/b/id/12315028-M.jpg'),
('Sefiller', 'Victor Hugo', 1862, 'Klasik Edebiyat', 1488, 'Paris, Fransa', 9789750778901, 'Fransız edebiyatının en önemli eserlerinden biri. Jean Valjean\'ın hayatındaki dönüşümü ve 19. yüzyıl Fransa\'sının toplumsal sorunlarını anlatan bu dev roman, dünya edebiyatının en etkileyici eserlerinden biridir.', 'https://covers.openlibrary.org/b/id/12218285-M.jpg'),
('Anna Karenina', 'Lev Tolstoy', 1877, 'Klasik Edebiyat', 864, 'Moskova, Rusya', 9789750789012, 'Rus edebiyatının en büyük eserlerinden biri. Anna Karenina\'nın aşk ve evlilik arasında yaşadığı trajik çelişkiyi anlatan bu roman, dünya edebiyatının en derin psikolojik analizlerinden biridir.', 'https://covers.openlibrary.org/b/id/12639895-M.jpg'),
('Büyük Umutlar', 'Charles Dickens', 1861, 'Klasik Edebiyat', 544, 'Londra, İngiltere', 9789750790123, 'İngiliz edebiyatının önemli eserlerinden biri. Pip adlı yetim bir çocuğun hayatındaki değişimleri ve sosyal sınıf farklılıklarının insan üzerindeki etkisini anlatan bu roman, dünya edebiyatının en etkileyici eserlerinden biridir.', 'https://covers.openlibrary.org/b/id/12310271-M.jpg'),
('Karamazov Kardeşler', 'Fyodor Dostoyevski', 1880, 'Klasik Edebiyat', 796, 'Sankt Petersburg, Rusya', 9789750791234, 'Dostoyevski\'nin son ve en önemli eseridir. Karamazov ailesinin dört oğlunun babalarının ölümü etrafında yaşadığı dramı anlatan bu roman, dünya edebiyatının en derin felsefi analizlerinden biridir.', 'https://covers.openlibrary.org/b/id/10848852-M.jpg'),
('Çalıkuşu', 'Reşat Nuri Güntekin', 1922, 'Türk Edebiyatı', 448, 'İstanbul, Türkiye', 9789750712345, 'Türk edebiyatının en önemli eserlerinden biridir. Feride\'nin öğretmenlik yaparken yaşadığı zorlukları ve aşk hayatındaki trajedileri anlatan bu roman, Türk edebiyatının en sevilen eserlerinden biridir.', 'https://covers.openlibrary.org/b/id/8231856-L.jpg'),
('Tutunamayanlar', 'Oğuz Atay', 1972, 'Türk Edebiyatı', 724, 'İstanbul, Türkiye', 9789750723456, 'Türk edebiyatında postmodern akımın öncü eserlerinden biridir. Turgut Özben\'in arkadaşı Selim Işık\'ın intiharından sonra onun geçmişini araştırması ve kendini keşfetmesi sürecini anlatan bu roman, Türk edebiyatının en önemli eserlerinden biri kabul edilir.', 'https://covers.openlibrary.org/b/id/10031512-M.jpg'),
('Kürk Mantolu Madonna', 'Sabahattin Ali', 1943, 'Türk Edebiyatı', 160, 'İstanbul, Türkiye', 9789750734567, 'Türk edebiyatının en önemli eserlerinden biridir. Raif Efendi\'nin Berlin\'de yaşadığı aşk hikayesini anlatan bu roman, Türk edebiyatının en etkileyici aşk hikayelerinden biridir.', 'https://covers.openlibrary.org/b/id/10031512-M.jpg'),
('Memleketimden İnsan Manzaraları', 'Nâzım Hikmet', 1966, 'Türk Edebiyatı', 2000, 'İstanbul, Türkiye', 9789750745678, 'Türk edebiyatının en önemli eserlerinden biridir. Türk toplumunun farklı kesimlerinden insanların hikayelerini anlatan bu destansı şiir, Türk edebiyatının en kapsamlı toplumsal analizlerinden biridir.', 'https://covers.openlibrary.org/b/id/14994023-M.jpg'),
('Kara Kitap', 'Orhan Pamuk', 1990, 'Türk Edebiyatı', 448, 'İstanbul, Türkiye', 9789750756789, 'Postmodern Türk edebiyatının önemli örneklerinden biridir. Galip\'in kaybolan eşi Rüya\'yı arama sürecinde İstanbul\'un gizemli sokaklarında yaşadığı maceraları anlatan bu roman, Türk edebiyatının önemli eserlerinden biridir.', 'https://covers.openlibrary.org/b/id/12297420-M.jpg'),
('1984', 'George Orwell', 1949, 'Edebiyat', 328, 'Londra, İngiltere', 9789750767891, 'Distopik edebiyatın başyapıtlarından biri. Totaliter bir rejimde yaşayan Winston Smith\'in hayatını anlatan bu roman, güç, kontrol ve özgürlük temalarını işler.', 'https://images.unsplash.com/photo-1544947950-fa07a98d237f?w=400&h=600&fit=crop'),
('Hayvan Çiftliği', 'George Orwell', 1945, 'Edebiyat', 112, 'Londra, İngiltere', 9789750767892, 'Alegorik bir roman. Çiftlik hayvanlarının insanlara karşı ayaklanmasını ve sonrasında yaşananları anlatan bu eser, siyasi sistemlerin eleştirisini yapar.', 'https://images.unsplash.com/photo-1481627834876-b7833e8f5570?w=400&h=600&fit=crop'),
('Fareler ve İnsanlar', 'John Steinbeck', 1937, 'Edebiyat', 112, 'New York, ABD', 9789750767893, 'Büyük Buhran döneminde iki göçmen işçinin hikayesini anlatan bu roman, dostluk, yalnızlık ve Amerikan Rüyası temalarını işler.', 'https://images.unsplash.com/photo-1512820790803-83ca734da794?w=400&h=600&fit=crop'),
('Gazap Üzümleri', 'John Steinbeck', 1939, 'Edebiyat', 464, 'New York, ABD', 9789750767894, 'Büyük Buhran döneminde Oklahoma\'dan Kaliforniya\'ya göç eden bir ailenin hikayesini anlatan bu roman, toplumsal adaletsizlik ve insan dayanışması temalarını işler.', 'https://images.unsplash.com/photo-1513475382585-d06e58bcb0e0?w=400&h=600&fit=crop'),
('Gurur ve Önyargı', 'Jane Austen', 1813, 'Edebiyat', 432, 'Londra, İngiltere', 9789750767895, '19. yüzyıl İngiliz toplumunda geçen bu aşk romanı, sosyal sınıflar, evlilik ve kadının toplumdaki yeri temalarını işler.', 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=400&h=600&fit=crop'),
('İkigai', 'Héctor García', 2016, 'Kişisel Gelişim', 208, 'Barselona, İspanya', 9789750767896, 'Japon felsefesinden esinlenen bu kitap, yaşam amacını bulma ve uzun ömürlü yaşama sanatını anlatır.', 'https://images.unsplash.com/photo-1544947950-fa07a98d237f?w=400&h=600&fit=crop'),
('Minimalizm', 'Joshua Fields Millburn', 2011, 'Kişisel Gelişim', 240, 'Ohio, ABD', 9789750767897, 'Modern yaşamın karmaşasından kurtulmak için minimalizm felsefesini anlatan bu kitap, sade yaşamın faydalarını gösterir.', 'https://images.unsplash.com/photo-1481627834876-b7833e8f5570?w=400&h=600&fit=crop'),
('Atomic Habits', 'James Clear', 2018, 'Kişisel Gelişim', 320, 'New York, ABD', 9789750767898, 'Küçük alışkanlıkların büyük değişimlere yol açabileceğini anlatan bu kitap, etkili alışkanlık oluşturma yöntemlerini sunar.', 'https://images.unsplash.com/photo-1512820790803-83ca734da794?w=400&h=600&fit=crop'),
('Deep Work', 'Cal Newport', 2016, 'Kişisel Gelişim', 304, 'New York, ABD', 9789750767899, 'Dikkat dağınıklığı çağında odaklanma sanatını anlatan bu kitap, derin çalışma tekniklerini öğretir.', 'https://images.unsplash.com/photo-1513475382585-d06e58bcb0e0?w=400&h=600&fit=crop'),
('Mindset', 'Carol S. Dweck', 2006, 'Kişisel Gelişim', 288, 'New York, ABD', 9789750767900, 'Sabit ve gelişim odaklı zihniyet türlerini inceleyen bu kitap, başarı ve öğrenme süreçlerini anlatır.', 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=400&h=600&fit=crop'),
('Sapiens', 'Yuval Noah Harari', 2011, 'Kültür & Toplum', 464, 'Tel Aviv, İsrail', 9789750767901, 'İnsanlığın kısa tarihini anlatan bu kitap, Homo sapiens\'in dünyayı nasıl ele geçirdiğini ve modern toplumları nasıl şekillendirdiğini anlatır.', 'https://images.unsplash.com/photo-1544947950-fa07a98d237f?w=400&h=600&fit=crop'),
('Homo Deus', 'Yuval Noah Harari', 2015, 'Kültür & Toplum', 448, 'Tel Aviv, İsrail', 9789750767902, 'Gelecekte insanlığın nasıl evrileceğini ve yapay zeka çağında insanın rolünü inceleyen bu kitap, gelecek vizyonları sunar.', 'https://images.unsplash.com/photo-1481627834876-b7833e8f5570?w=400&h=600&fit=crop'),
('Guns, Germs, and Steel', 'Jared Diamond', 1997, 'Kültür & Toplum', 480, 'New York, ABD', 9789750767903, 'İnsan toplumlarının neden farklı şekillerde geliştiğini açıklayan bu kitap, coğrafya, biyoloji ve kültürün etkileşimini inceler.', 'https://images.unsplash.com/photo-1512820790803-83ca734da794?w=400&h=600&fit=crop'),
('The Tipping Point', 'Malcolm Gladwell', 2000, 'Kültür & Toplum', 304, 'New York, ABD', 9789750767904, 'Küçük değişikliklerin nasıl büyük etkiler yaratabileceğini anlatan bu kitap, sosyal epidemilerin nasıl yayıldığını inceler.', 'https://images.unsplash.com/photo-1513475382585-d06e58bcb0e0?w=400&h=600&fit=crop'),
('Outliers', 'Malcolm Gladwell', 2008, 'Kültür & Toplum', 309, 'New York, ABD', 9789750767905, 'Başarılı insanların hikayelerini inceleyen bu kitap, başarının arkasındaki gizli faktörleri ortaya çıkarır.', 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=400&h=600&fit=crop'),
('The Innovators', 'Walter Isaacson', 2014, 'Teknoloji', 560, 'New York, ABD', 9789750767906, 'Dijital devrimin öncülerinin hikayelerini anlatan bu kitap, bilgisayar ve internet çağının nasıl başladığını anlatır.', 'https://images.unsplash.com/photo-1544947950-fa07a98d237f?w=400&h=600&fit=crop'),
('Steve Jobs', 'Walter Isaacson', 2011, 'Teknoloji', 656, 'New York, ABD', 9789750767907, 'Apple\'ın kurucusu Steve Jobs\'ın hayatını anlatan bu biyografi, teknoloji dünyasının en etkili isimlerinden birinin hikayesini sunar.', 'https://images.unsplash.com/photo-1481627834876-b7833e8f5570?w=400&h=600&fit=crop'),
('The Code Book', 'Simon Singh', 1999, 'Teknoloji', 432, 'Londra, İngiltere', 9789750767908, 'Şifreleme tarihini anlatan bu kitap, antik çağlardan günümüze kadar şifreleme tekniklerinin gelişimini inceler.', 'https://images.unsplash.com/photo-1512820790803-83ca734da794?w=400&h=600&fit=crop'),
('The Art of Computer Programming', 'Donald E. Knuth', 1968, 'Teknoloji', 672, 'California, ABD', 9789750767909, 'Bilgisayar programlama alanının en kapsamlı eserlerinden biri. Algoritma ve veri yapılarını detaylı bir şekilde anlatır.', 'https://images.unsplash.com/photo-1513475382585-d06e58bcb0e0?w=400&h=600&fit=crop'),
('Clean Code', 'Robert C. Martin', 2008, 'Teknoloji', 464, 'New Jersey, ABD', 9789750767910, 'Yazılım geliştirme alanında temiz kod yazma prensiplerini anlatan bu kitap, programcılar için vazgeçilmez bir rehber.', 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=400&h=600&fit=crop'),
('The Story of Art', 'E.H. Gombrich', 1950, 'Sanat', 688, 'Londra, İngiltere', 9789750767911, 'Sanat tarihinin en kapsamlı eserlerinden biri. İlk çağlardan günümüze kadar sanatın gelişimini anlatır.', 'https://images.unsplash.com/photo-1544947950-fa07a98d237f?w=400&h=600&fit=crop'),
('Ways of Seeing', 'John Berger', 1972, 'Sanat', 176, 'Londra, İngiltere', 9789750767912, 'Görsel sanatları nasıl yorumlayacağımızı öğreten bu kitap, sanat eleştirisi alanında çığır açan bir eser.', 'https://images.unsplash.com/photo-1481627834876-b7833e8f5570?w=400&h=600&fit=crop'),
('The Art Book', 'Phaidon Editors', 1994, 'Sanat', 512, 'Londra, İngiltere', 9789750767913, '500 büyük sanatçının eserlerini tanıtan bu kitap, sanat tarihinin en önemli yapıtlarını gözler önüne serer.', 'https://images.unsplash.com/photo-1512820790803-83ca734da794?w=400&h=600&fit=crop'),
('Art and Visual Perception', 'Rudolf Arnheim', 1954, 'Sanat', 508, 'California, ABD', 9789750767914, 'Görsel algı ve sanat arasındaki ilişkiyi inceleyen bu kitap, psikoloji ve sanatın kesişim noktalarını araştırır.', 'https://images.unsplash.com/photo-1513475382585-d06e58bcb0e0?w=400&h=600&fit=crop'),
('The Art of Color', 'Johannes Itten', 1961, 'Sanat', 96, 'Zürih, İsviçre', 9789750767915, 'Renk teorisi ve uygulamasını anlatan bu kitap, sanatçılar ve tasarımcılar için temel bir kaynak.', 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=400&h=600&fit=crop'),
('Pedagogy of the Oppressed', 'Paulo Freire', 1968, 'Eğitim', 184, 'São Paulo, Brezilya', 9789750767916, 'Eğitim felsefesi alanında çığır açan bu kitap, ezilenlerin eğitimi konusunu ele alır ve özgürleştirici eğitim yöntemlerini önerir.', 'https://images.unsplash.com/photo-1544947950-fa07a98d237f?w=400&h=600&fit=crop'),
('How Children Learn', 'John Holt', 1967, 'Eğitim', 320, 'New York, ABD', 9789750767917, 'Çocukların doğal öğrenme süreçlerini inceleyen bu kitap, geleneksel eğitim sisteminin eleştirisini yapar.', 'https://images.unsplash.com/photo-1481627834876-b7833e8f5570?w=400&h=600&fit=crop'),
('The Montessori Method', 'Maria Montessori', 1909, 'Eğitim', 416, 'Roma, İtalya', 9789750767918, 'Montessori eğitim yönteminin temellerini anlatan bu kitap, çocuk merkezli eğitim yaklaşımını detaylandırır.', 'https://images.unsplash.com/photo-1512820790803-83ca734da794?w=400&h=600&fit=crop'),
('Democracy and Education', 'John Dewey', 1916, 'Eğitim', 378, 'New York, ABD', 9789750767919, 'Demokratik eğitim felsefesini anlatan bu kitap, eğitim ve demokrasi arasındaki ilişkiyi inceler.', 'https://images.unsplash.com/photo-1513475382585-d06e58bcb0e0?w=400&h=600&fit=crop'),
('The Art of Learning', 'Josh Waitzkin', 2007, 'Eğitim', 288, 'New York, ABD', 9789750767920, 'Satranç şampiyonu ve dövüş sanatları ustası olan yazarın öğrenme süreçlerini anlatan bu kitap, performans psikolojisini inceler.', 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=400&h=600&fit=crop'),
('The Life-Changing Magic of Tidying Up', 'Marie Kondo', 2010, 'Yaşam Tarzı', 224, 'Tokyo, Japonya', 9789750767921, 'KonMari yöntemi olarak bilinen düzenleme tekniğini anlatan bu kitap, sadece sevdiğiniz eşyaları tutma felsefesini öğretir.', 'https://images.unsplash.com/photo-1544947950-fa07a98d237f?w=400&h=600&fit=crop'),
('The Power of Habit', 'Charles Duhigg', 2012, 'Yaşam Tarzı', 371, 'New York, ABD', 9789750767922, 'Alışkanlıkların bilimsel temellerini anlatan bu kitap, iyi alışkanlıklar oluşturma ve kötü alışkanlıkları değiştirme yöntemlerini sunar.', 'https://images.unsplash.com/photo-1481627834876-b7833e8f5570?w=400&h=600&fit=crop'),
('The 4-Hour Workweek', 'Timothy Ferriss', 2007, 'Yaşam Tarzı', 308, 'New York, ABD', 9789750767923, 'Çalışma hayatını optimize etme ve daha fazla zaman kazanma yöntemlerini anlatan bu kitap, yaşam tarzı tasarımı konusunu ele alır.', 'https://images.unsplash.com/photo-1512820790803-83ca734da794?w=400&h=600&fit=crop'),
('Essentialism', 'Greg McKeown', 2014, 'Yaşam Tarzı', 272, 'New York, ABD', 9789750767924, 'Sadece önemli şeylere odaklanma sanatını anlatan bu kitap, hayatı sadeleştirme ve öncelikleri belirleme konusunda rehberlik eder.', 'https://images.unsplash.com/photo-1513475382585-d06e58bcb0e0?w=400&h=600&fit=crop'),
('The Happiness Project', 'Gretchen Rubin', 2009, 'Yaşam Tarzı', 320, 'New York, ABD', 9789750767925, 'Mutluluk üzerine yapılan bir yıllık deneyi anlatan bu kitap, günlük yaşamda mutluluğu artırma yöntemlerini sunar.', 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=400&h=600&fit=crop');
